sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {


            api.emit('MACROPONENT_VIEWPORT_LOAD_REQUESTED', {
                viewportElementId: 'modal_viewport',
                route: 'sowformmodal',
                fields: {
                    table: api.context.props.table,
                    sysId: api.context.props.recordSysId,
                    title: "Assign",
                },
                params: {
                    "modalPath": "assign_modal",
                    "fields": JSON.stringify(api.context.props.fields),
                    "requiredFields": ["assignment_group", "assigned_to", "work_notes"],
                    "isGFormSave": "true",
                }
            });


     //api.emit('SOW_RECORD_UI_ACTION#EXECUTE_UI_ACTION', {
              //  actionSysId : "960bebc78342ca10c54c1e60ceaad369"
          //  });
}
