sys_ux_data_broker_transform.required_translations:
[ {
  "message" : "Please specify the task sys_id",
  "comment" : ""
}, {
  "message" : "Please specify the task table",
  "comment" : ""
}, {
  "message" : "Task ID",
  "comment" : ""
}, {
  "message" : "Task Table",
  "comment" : ""
} ]

sys_ux_data_broker_transform.script:
function transform(input) {
	var response = {
		canCreate: false,
		canAddNotes: false
	};
	var table = input.taskTable;
	if (gs.nil(table)) {
		table = JiraCustomUtils.TABLES.TASK;
	}
	if (gs.nil(input.taskID)) {
		return response;
	}
	var task = new GlideRecord(table);
	if (!task.get(input.taskID)) {
		return response;
	}
	var utils = new JiraCustomUtils();
	response.canCreate = utils.isEligibleForCreatingIssue(task);
	response.canAddNotes = utils.isEligibleForUpdatingIssue(task);
	return response;
}
